mapper.select:
SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM (
SELECT y.*, ROWNUM AS rn
FROM youth_policy_detail y
WHERE district_code = #{districtCode, jdbcType=VARCHAR} 

  AND category_code = #{categoryCode, jdbcType=VARCHAR} 

AND ROWNUM <= 100
)
WHERE rn > 0

SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM (
SELECT y.*, ROWNUM AS rn
FROM youth_policy_detail y
WHERE policy_code = #{policyCode, jdbcType=VARCHAR}
AND category_code = #{categoryCode, jdbcType=VARCHAR}
AND district_code = #{districtCode, jdbcType=VARCHAR}
AND ROWNUM <= 100
)
WHERE rn > 0

SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM youth_policy_detail
WHERE policy_code = #{policyCode, jdbcType=VARCHAR} AND category_code = #{categoryCode, jdbcType=VARCHAR}

