mapper.select:
SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM (
SELECT y.*, ROWNUM AS rn
FROM youth_policy_detail y
WHERE policy_code = #{policyCode}
AND category_code = #{categoryCode}
AND district_code = #{districtCode}
AND ROWNUM <= 100
)
WHERE rn > 0

SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM youth_policy_detail
WHERE policy_code = #{policyCode} AND category_code = #{categoryCode}

SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM youth_policy_detail
WHERE policy_code = #{policyCode} AND category_code = #{categoryCode}

SELECT policy_code, policy_name, category_code, policy_content, policy_start_date, policy_end_date
FROM youth_policy_detail
WHERE policy_code = #{policyCode} AND category_code = #{categoryCode}

